tinymce.PluginManager.add('embed', function(editor, url) {
    editor.addButton('embed', {
        title: 'Embed',
        image: 'https://assets.gridoto.com/file/2017/cms/icon/twitter.jpg',
        onclick: function() {
            var title = 'Add Embed';
            var src = base_url + 'popup/embed';
            $('#general-modal-title').html(title);
            $('#general-modal-iframe').attr('src',src);
            $('#general-modal').modal('show');
        }
    });
});