tinymce.PluginManager.add('video', function(editor, url) {
    editor.addButton('video', {
        title: 'Insert video',
        image: 'https://assets.gridoto.com/file/2017/cms/icon/video.png',
        onclick: function() {
            var title = 'Add Video';
            var src = base_url + 'video/index?popup=1&module=article';
            $('#general-modal-title').html(title);
            $('#general-modal-iframe').attr('src',src);
            $('#general-modal').modal('show');
        }
    });
});