/*
  Note: We have included the plugin in the same JavaScript file as the TinyMCE
  instance for display purposes only. Tiny recommends not maintaining the plugin
  with the TinyMCE instance and using the `external_plugins` option.
*/

tinymce.PluginManager.add('editphototext', function(editor, url) {
  // var e = editor.selection.getNode();
  
  var openDialog = function () {    
    // console.log(editor.selection.getNode().getAttribute('data-caption'))
  return editor.windowManager.open({
    title: 'Edit Caption & Author',
    body: {
      type: 'panel',
      items: [
        {
          type: 'textarea',
          name: 'caption',
          label: 'Caption'
        },
        {
          type: 'textarea',
          name: 'author',
          label: 'Author'
        }
      ]
    },
    initialData: {
        caption: editor.selection.getNode().getAttribute('data-caption'),
        author: editor.selection.getNode().getAttribute('data-author')
    },
    buttons: [
        {
          type: 'cancel',
          text: 'Close'
        },
        {
          type: 'submit',
          text: 'Update',
          primary: true
        }
    ],
    onSubmit: function (api) {
        var data = api.getData();
        editor.selection.getNode().setAttribute('data-caption', data.caption);
        editor.selection.getNode().setAttribute('data-author', data.author);
        api.close();
    }            
});
  }

      /* Add a button that opens a window */
      editor.ui.registry.addIcon('editphototext', '<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M7.127 22.562l-7.127 1.438 1.438-7.128 5.689 5.69zm1.414-1.414l11.228-11.225-5.69-5.692-11.227 11.227 5.689 5.69zm9.768-21.148l-2.816 2.817 5.691 5.691 2.816-2.819-5.691-5.689z"/></svg>' );
      editor.ui.registry.addButton('editphototext', {
        tooltip: 'Edit Caption & Author',
        icon : "editphototext",
        onAction: function () {
          /* Open window */
          openDialog();
        }
      });
      /* Adds a menu item, which can then be included in any menu via the menu/menubar configuration */
      editor.ui.registry.addMenuItem('editphototext', {
        text: 'Edit Caption & Author',
        onAction: function() {
          /* Open window */
          openDialog();
        }
      });
  

    /* Return the metadata for the help plugin */
    return {
      getMetadata: function () {
        return  {
          name: 'editphototext plugin'
        };
      }
    };


  });