tinymce.PluginManager.add('photos', function (editor, url) {
    editor.ui.registry.addButton('photos', {
        title: 'Insert image',
        icon: 'image',
        onAction: function () {
            var id_tinymce = editor.id;

            var title = 'Choose Photo';
            var src = base_url + '/photo?modals=true&id_tinymce=' + id_tinymce;
            $('#general-modal-title').html(title);
            $('#general-modal-iframe').attr('src', src);
            $('#general-modal').modal('show');
        }
    });
});